import React, { useState } from 'react';

const Numbers = () => {
	const [numbers, setNumbers] = useState(['one', 'two', 'three']);

	const addNumber = () => {
		setNumbers(['four'])
	}
	return (
		<div>
		<h1>Numbers</h1>

			{ numbers.map( num => {
				return <h4 onClick={addNumber}>{ num }</h4>
			}		)
			}
		</div>
)
}

export default Numbers;
