        fetch(`http://127.0.0.1:8000/api/movies/${mov.id}/rate_movie/`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Token ${TOKEN}`
          },
          body: JSON.stringify( body )
        })
        .then( resp => resp.json())
        .then( resp => getDetails())
        .catch( error => console.log(error))
        )
     }
}
