import React , { useState } from 'react';

function MovieForm(props) {

	const [ title, setTitle ] = useState(props.move.title)
	const [ description, setDescription ] = useState(props.move.title)

	return (
		<React.Fragment>
		{ props.movie ?(
			<div>
			<label for ="title"> Title </label>
			<br/>
			<input id="title" type="text" palceholder="title" value={title}
			onChange={evt => setTitle(evt.target.value)}/>
			<label for="description" >Description</label>
			<br/>
			<textarea type="text" id="description" palceholder="Description"></textarea>
			</div>
		) : null}
		</React.Fragment>
		)
}

export default MovieForm;
